INSERT INTO `cms_article` VALUES ('9', 'ismobile()和js插件 判断来源切换到不能的模板', '4', '', '有时候前端框架网页自适应适配的不好，编写也比较困难，就设计成可以切换到不能模板。在thinkphp中有看到ismobile()的方法，非常好用，用来判断客户端设备，除了后端的检测外，前端也想加判断。\r\njavascripte版本的isMobile()', '', '<span style=\"color:#444444;font-family:\'Microsoft YaHei\', Helvetica, Arial, \'Lucida Grande\', Tahoma, sans-serif;font-size:15px;line-height:27px;background-color:#FFFFFF;\">有时候前端框架网页自适应适配的不好，编写也比较困难，就设计成可以切换到不能模板。在thinkphp中有看到ismobile()的方法，非常好用，用来判断客户端设备，除了后端的检测外，前端也想加判断。</span>\r\n<p style=\"font-family:\'Microsoft YaHei\', Helvetica, Arial, \'Lucida Grande\', Tahoma, sans-serif;font-size:15px;vertical-align:baseline;text-align:justify;text-indent:2em;color:#444444;background-color:#FFFFFF;\">\r\n	javascripte版本的isMobile()\r\n</p>\r\n<p style=\"font-family:\'Microsoft YaHei\', Helvetica, Arial, \'Lucida Grande\', Tahoma, sans-serif;font-size:15px;vertical-align:baseline;text-align:justify;text-indent:2em;color:#444444;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-family:\'Microsoft YaHei\', Helvetica, Arial, \'Lucida Grande\', Tahoma, sans-serif;font-size:15px;vertical-align:baseline;text-align:justify;text-indent:2em;color:#444444;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<div class=\"line number1 index0 alt2\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333px;color:#444444;border:0px !important;margin:0px !important;padding:0px 1em !important;vertical-align:baseline !important;background:none white !important;\">\r\n	$(document).ready(function() {\r\n</div>\r\n<div class=\"line number2 index1 alt1\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333px;color:#444444;border:0px !important;margin:0px !important;padding:0px 1em !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var isMobile = {\r\n</div>\r\n<div class=\"line number3 index2 alt2\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333px;color:#444444;border:0px !important;margin:0px !important;padding:0px 1em !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;Android: function() {\r\n</div>\r\n<div class=\"line number4 index3 alt1\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333px;color:#444444;border:0px !important;margin:0px !important;padding:0px 1em !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return navigator.userAgent.match(/Android/i) ? true : false;\r\n</div>\r\n<div class=\"line number5 index4 alt2\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333px;color:#444444;border:0px !important;margin:0px !important;padding:0px 1em !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;},\r\n</div>\r\n<div class=\"line number6 index5 alt1\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333px;color:#444444;border:0px !important;margin:0px !important;padding:0px 1em !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;BlackBerry: function() {\r\n</div>\r\n<div class=\"line number7 index6 alt2\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333px;color:#444444;border:0px !important;margin:0px !important;padding:0px 1em !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return navigator.userAgent.match(/BlackBerry/i) ? true : false;\r\n</div>\r\n<div class=\"line number8 index7 alt1\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333px;color:#444444;border:0px !important;margin:0px !important;padding:0px 1em !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;},\r\n</div>\r\n<div class=\"line number9 index8 alt2\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333px;color:#444444;border:0px !important;margin:0px !important;padding:0px 1em !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;iOS: function() {\r\n</div>\r\n<div class=\"line number10 index9 alt1\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333px;color:#444444;border:0px !important;margin:0px !important;padding:0px 1em !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return navigator.userAgent.match(/iPhone|iPad|iPod/i) ? true : false;\r\n</div>\r\n<div class=\"line number11 index10 alt2\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333px;color:#444444;border:0px !important;margin:0px !important;padding:0px 1em !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;},\r\n</div>\r\n<div class=\"line number12 index11 alt1\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333px;color:#444444;border:0px !important;margin:0px !important;padding:0px 1em !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;Windows: function() {\r\n</div>\r\n<div class=\"line number13 index12 alt2\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333px;color:#444444;border:0px !important;margin:0px !important;padding:0px 1em !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return navigator.userAgent.match(/IEMobile/i) ? true : false;\r\n</div>\r\n<div class=\"line number14 index13 alt1\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333px;color:#444444;border:0px !important;margin:0px !important;padding:0px 1em !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;},\r\n</div>\r\n<div class=\"line number15 index14 alt2\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333px;color:#444444;border:0px !important;margin:0px !important;padding:0px 1em !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;any: function() {\r\n</div>\r\n<div class=\"line number16 index15 alt1\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333px;color:#444444;border:0px !important;margin:0px !important;padding:0px 1em !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return (isMobile.Android() || isMobile.BlackBerry() || isMobile.iOS() || isMobile.Windows());\r\n</div>\r\n<div class=\"line number17 index16 alt2\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333px;color:#444444;border:0px !important;margin:0px !important;padding:0px 1em !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;}\r\n</div>\r\n<div class=\"line number18 index17 alt1\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333px;color:#444444;border:0px !important;margin:0px !important;padding:0px 1em !important;vertical-align:baseline !important;background:none white !important;\">\r\n	};\r\n</div>\r\n<div class=\"line number19 index18 alt2\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333px;color:#444444;border:0px !important;margin:0px !important;padding:0px 1em !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if( isMobile.any() )\r\n</div>\r\n<div class=\"line number20 index19 alt1\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333px;color:#444444;border:0px !important;margin:0px !important;padding:0px 1em !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{\r\n</div>\r\n<div class=\"line number21 index20 alt2\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333px;color:#444444;border:0px !important;margin:0px !important;padding:0px 1em !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$(\'.main_header\').hide();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n</div>\r\n<div class=\"line number22 index21 alt1\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333px;color:#444444;border:0px !important;margin:0px !important;padding:0px 1em !important;vertical-align:baseline !important;background:none white !important;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\r\n</div>\r\n<div class=\"line number23 index22 alt2\" style=\"font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace;font-size:13.3333px;color:#444444;border:0px !important;margin:0px !important;padding:0px 1em !important;vertical-align:baseline !important;background:none white !important;\">\r\n	});\r\n</div>\r\n<span style=\"color:#444444;font-family:\'Microsoft YaHei\', Helvetica, Arial, \'Lucida Grande\', Tahoma, sans-serif;font-size:15px;line-height:27px;background-color:#FFFFFF;\">另外有一个简单的JS库，用来检测移动设备</span><br />\r\n<div style=\"border:0px;font-family:\'Microsoft YaHei\', Helvetica, Arial, \'Lucida Grande\', Tahoma, sans-serif;font-size:15px;margin:0px;padding:0px;vertical-align:baseline;color:#444444;background-color:#FFFFFF;\">\r\n	<br />\r\n</div>\r\n<p style=\"font-family:\'Microsoft YaHei\', Helvetica, Arial, \'Lucida Grande\', Tahoma, sans-serif;font-size:15px;vertical-align:baseline;text-align:justify;text-indent:2em;color:#444444;background-color:#FFFFFF;\">\r\n	github地址： https://github.com/kaimallea/isMobile<br />\r\n<br />\r\n示例<br />\r\n(function () {<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var MOBILE_SITE = \'http://m.xx.com/index.html\',&nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; NO_REDIRECT = \'noredirect\';<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; if (isMobile.any) {<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; if ( document.cookie.indexOf(NO_REDIRECT) === -1 ) {<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; document.location = MOBILE_SITE;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; })();\r\n</p>\r\n<p style=\"font-family:\'Microsoft YaHei\', Helvetica, Arial, \'Lucida Grande\', Tahoma, sans-serif;font-size:15px;vertical-align:baseline;text-align:justify;text-indent:2em;color:#444444;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-family:\'Microsoft YaHei\', Helvetica, Arial, \'Lucida Grande\', Tahoma, sans-serif;font-size:15px;vertical-align:baseline;text-align:justify;text-indent:2em;color:#444444;background-color:#FFFFFF;\">\r\n	Why use isMobile?<br />\r\n<br />\r\n<br />\r\nI had a specific requirement for a project when I created this:<br />\r\n<br />\r\n<br />\r\n- Redirect all iPhones, iPods, Android phones, and seven inch devices to the mobile site.<br />\r\n<br />\r\n<br />\r\nA completely separate site had been created for mobile devices, so feature detection/graceful degredation/progressive enhancement were out of the question. I had to redirect.<br />\r\n<br />\r\n<br />\r\nI couldn’t do detection on the back-end, because the entire site was cached and served by Akamai; I had to do the detection client-side.<br />\r\n<br />\r\n<br />\r\nSo I resorted to UA sniffing.<br />\r\n<br />\r\n<br />\r\nI tried to keep the script small ( currently ~1.3k bytes, minified ) and simple, because it would need to execute in the &nbsp; &lt;head&gt; , which is generally a bad idea, since JS blocks downloading and rendering of anything else while it parses and executes. In the case of mobile redirection, I don’t mind so much, because I want to start the redirect as soon as possible, before the device has a chance to start downloading and rendering stuff. For non-mobile platforms, the script should execute fast, so the browser can quickly get back to downloading and rendering.<br />\r\n<br />\r\n<br />\r\nHow it works<br />\r\n<br />\r\n<br />\r\nisMobile runs quickly on page load to detect mobile devices; it then creates a JavaScript object with the results.<br />\r\n<br />\r\n<br />\r\nDevices detected by isMobile<br />\r\n<br />\r\n<br />\r\nThe following properies of the isMobile &nbsp; object will either be &nbsp; true &nbsp; or &nbsp; false<br />\r\n<br />\r\n<br />\r\nApple devices<br />\r\n<br />\r\n<br />\r\nisMobile.apple.phone<br />\r\nisMobile.apple.ipod<br />\r\nisMobile.apple.tablet<br />\r\nisMobile.apple.device &nbsp; (any mobile Apple device)<br />\r\nAndroid devices<br />\r\n<br />\r\n<br />\r\nisMobile.android.phone<br />\r\nisMobile.android.tablet<br />\r\nisMobile.android.device &nbsp; (any mobile Android device)<br />\r\nWindows devices<br />\r\n<br />\r\n<br />\r\nisMobile.windows.phone<br />\r\nisMobile.windows.tablet<br />\r\nisMobile.windows.device &nbsp; (any mobile Windows device)<br />\r\nSpecific seven inch devices<br />\r\n<br />\r\n<br />\r\nisMobile.seven_inch<br />\r\ntrue &nbsp; if the device is one of the following 7″ devices:<br />\r\nNexus 7<br />\r\nKindle Fire<br />\r\nNook Tablet 7 inch<br />\r\nGalaxy Tab 7 inch<br />\r\n“Other” devices<br />\r\n<br />\r\n<br />\r\nisMobile.other_blackberry_10<br />\r\nisMobile.other_blackberry<br />\r\nisMobile.other_opera &nbsp; (Opera Mini)<br />\r\nisMobile.other_firefox<br />\r\nAggregate Groupings<br />\r\n<br />\r\n<br />\r\nisMobile.any &nbsp; - any device matched<br />\r\nisMobile.phone &nbsp; - any device in the ‘phone’ groups above<br />\r\nisMobile.tablet &nbsp; - any device in the ‘tablet’ groups above\r\n</p>', '1', '0', '1', '0', '8', '0', '0', '2017-08-24 08:46:26', '0000-00-00 00:00:00');
INSERT INTO `cms_article` VALUES ('10', 'mysql id自增添加和修改语句', '7', '', '', '', '<span style=\"color:#444444;font-family:\'Microsoft YaHei\', Helvetica, Arial, \'Lucida Grande\', Tahoma, sans-serif;font-size:15px;line-height:27px;background-color:#FFFFFF;\">本来user表的useid不是自增的。 &nbsp;我们把userid改为自增。</span><br />\r\n<span style=\"color:#444444;font-family:\'Microsoft YaHei\', Helvetica, Arial, \'Lucida Grande\', Tahoma, sans-serif;font-size:15px;line-height:27px;background-color:#FFFFFF;\">执行以下语句就可以把userid自增， 每次加1。</span><br />\r\n<span style=\"color:#444444;font-family:\'Microsoft YaHei\', Helvetica, Arial, \'Lucida Grande\', Tahoma, sans-serif;font-size:15px;line-height:27px;background-color:#FFFFFF;\">语句：alter table `user` change userid userid int not null auto_increment primary key;&nbsp;</span>', '0', '0', '1', '0', '10', '0', '0', '2017-08-24 08:49:24', '0000-00-00 00:00:00');
INSERT INTO `cms_article` VALUES ('11', 'android 居中菜单PopupWindow的使用', '13', '', '', '', 'PopupWindow<br />\r\nDisplayMetrics dm = new DisplayMetrics();<br />\r\nMessageReceiveActivity.this.getActivity().getWindowManager().getDefaultDisplay().getMetrics(dm); int width = (int)(dm.widthPixels/2.5);//宽度 &nbsp;LayoutInflater inflater = (LayoutInflater)MessageReceiveActivity.this.getActivity().getSystemService(Context.LAYOUT_INFLATER_SERVICE); final View vPopupWindow = inflater.inflate(R.layout.ui_messageoper_popup, null, false);<br />\r\nPopupWindow<br />\r\npw = new PopupWindow(vPopupWindow,width, ViewGroup.LayoutParams.WRAP_CONTENT,true);<br />\r\nvPopupWindow.setOnTouchListener(new View.OnTouchListener() { @Override &nbsp;public boolean onTouch(View view, MotionEvent motionEvent) { if (pw != null &amp;&amp; pw.isShowing()) {<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; pw.dismiss();<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; pw = null;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; } return false;<br />\r\n&nbsp; &nbsp; }<br />\r\n});<br />\r\nViewGroup fav = (ViewGroup)vPopupWindow.findViewById(R.id.m_fav_layout);<br />\r\nViewGroup copy = (ViewGroup)vPopupWindow.findViewById(R.id.m_copy_layout);<br />\r\nViewGroup del = (ViewGroup)vPopupWindow.findViewById(R.id.m_del_layout); //fav.setVisibility(View.GONE); //copy.setVisibility(View.GONE); del.setOnClickListener(new View.OnClickListener() { @Override &nbsp;public void onClick(View v) {<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; pw.dismiss();<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; pw = null;<br />\r\n&nbsp; &nbsp; }<br />\r\n});<br />\r\nvPopupWindow.startAnimation(AnimationUtils.loadAnimation(MessageReceiveActivity.this.getActivity(), R.anim.activity_translate_in));<br />\r\npw.showAtLocation(view, Gravity.CENTER, 0, 0);<br />', '0', '0', '1', '0', '8', '0', '0', '2017-08-24 08:51:08', '0000-00-00 00:00:00');
INSERT INTO `cms_article` VALUES ('12', 'java读取excel文件导出到数据库 excel转换到数据库', '5', '', '', '', '<span style=\"color:#444444;font-family:\'Microsoft YaHei\', Helvetica, Arial, \'Lucida Grande\', Tahoma, sans-serif;font-size:15px;line-height:27px;background-color:#FFFFFF;\">java读取excel文件导出到数据库 excel转换到数据库操作</span>\r\n<p style=\"font-family:\'Microsoft YaHei\', Helvetica, Arial, \'Lucida Grande\', Tahoma, sans-serif;font-size:15px;vertical-align:baseline;text-align:justify;text-indent:2em;color:#444444;background-color:#FFFFFF;\">\r\n	package xls2db.logic;\r\n</p>\r\n<p style=\"font-family:\'Microsoft YaHei\', Helvetica, Arial, \'Lucida Grande\', Tahoma, sans-serif;font-size:15px;vertical-align:baseline;text-align:justify;text-indent:2em;color:#444444;background-color:#FFFFFF;\">\r\n	import java.io.FileInputStream;<br />\r\nimport java.io.InputStream;<br />\r\nimport java.sql.Connection;<br />\r\nimport java.sql.Statement;\r\n</p>\r\n<p style=\"font-family:\'Microsoft YaHei\', Helvetica, Arial, \'Lucida Grande\', Tahoma, sans-serif;font-size:15px;vertical-align:baseline;text-align:justify;text-indent:2em;color:#444444;background-color:#FFFFFF;\">\r\n	import com.db.Db;\r\n</p>\r\n<p style=\"font-family:\'Microsoft YaHei\', Helvetica, Arial, \'Lucida Grande\', Tahoma, sans-serif;font-size:15px;vertical-align:baseline;text-align:justify;text-indent:2em;color:#444444;background-color:#FFFFFF;\">\r\n	import jxl.*;\r\n</p>\r\n<p style=\"font-family:\'Microsoft YaHei\', Helvetica, Arial, \'Lucida Grande\', Tahoma, sans-serif;font-size:15px;vertical-align:baseline;text-align:justify;text-indent:2em;color:#444444;background-color:#FFFFFF;\">\r\n	public class ExeclOper {<br />\r\njxl.Workbook rwb = null;<br />\r\nSheet rs = null;<br />\r\n<br />\r\nExeclOper(){&nbsp;&nbsp;&nbsp;&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp; try {<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //构建Workbook对象, 只读Workbook对象<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //直接从本地文件创建Workbook<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //从输入流创建Workbook<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; InputStream is = new FileInputStream(\"d:\\\\jxlrwtest.xls\");<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; rwb = Workbook.getWorkbook(is);<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /*<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; int sheets = rwb.getNumberOfSheets();<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Sheet[] sheets = rwb.getSheets();<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; String sheetName = rs.getName();<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; */<br />\r\n&nbsp;&nbsp;&nbsp; }catch (Exception e) {<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; e.printStackTrace();<br />\r\n&nbsp;&nbsp;&nbsp; }<br />\r\n}<br />\r\npublic String[] getExcel(int i){<br />\r\n&nbsp;&nbsp;&nbsp; //获取第一张Sheet表<br />\r\n&nbsp;&nbsp;&nbsp; rs = rwb.getSheet(0);<br />\r\n&nbsp;&nbsp;&nbsp; int rsColumns = rs.getColumns();//Cell[] cell = rs.getColumn(0);&nbsp;&nbsp;&nbsp;&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp; String[] dbrows = new String[rsColumns];<br />\r\n&nbsp;&nbsp;&nbsp; int rsRows = rs.getRows();//Cell[] cell = rs.getRow(0);&nbsp;&nbsp;&nbsp;&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp; Cell[] cell = rs.getRow(i);<br />\r\n&nbsp;&nbsp;&nbsp; if(cell.length&lt;rsColumns){ //一行有的单元格为空读不出来&nbsp;&nbsp;&nbsp;&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; for(int j=0;j&lt;cell.length;j++){&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; dbrows[j]=cell[j].getContents();<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp; }else {<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; for(int j=0;j&lt;rsColumns;j++){&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; dbrows[j]=cell[j].getContents();<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;<br />\r\n// 获取第一行，第一列的值<br />\r\n&nbsp;&nbsp;&nbsp; Cell c00 = rs.getCell(0, 0);<br />\r\n&nbsp;&nbsp;&nbsp; String strc00 = c00.getContents();<br />\r\n//&nbsp;&nbsp;&nbsp; 获取第一行，第二列的值<br />\r\n&nbsp;&nbsp;&nbsp; Cell c10 = rs.getCell(1, 0);<br />\r\n&nbsp;&nbsp;&nbsp; String strc10 = c10.getContents();<br />\r\n//&nbsp;&nbsp;&nbsp; 获取第二行，第二列的值<br />\r\n&nbsp;&nbsp;&nbsp; Cell c11 = rs.getCell(1, 1);<br />\r\n&nbsp;&nbsp;&nbsp; String strc11 = c11.getContents();<br />\r\n&nbsp;&nbsp;&nbsp; //System.out.println(\"Cell(0, 0)\" + \" value : \" + strc00 + \"; type : \" + c00.getType());<br />\r\n&nbsp;&nbsp;&nbsp; //System.out.println(\"Cell(1, 0)\" + \" value : \" + strc10 + \"; type : \" + c10.getType());<br />\r\n&nbsp;&nbsp;&nbsp; //System.out.println(\"Cell(1, 1)\" + \" value : \" + strc11 + \"; type : \" + c11.getType());\r\n</p>\r\n<p style=\"font-family:\'Microsoft YaHei\', Helvetica, Arial, \'Lucida Grande\', Tahoma, sans-serif;font-size:15px;vertical-align:baseline;text-align:justify;text-indent:2em;color:#444444;background-color:#FFFFFF;\">\r\n	&nbsp;&nbsp;&nbsp; //rwb.close();<br />\r\n&nbsp;&nbsp;&nbsp; return dbrows;<br />\r\n}<br />\r\n<br />\r\nprivate void write2db() throws Exception {<br />\r\n&nbsp;&nbsp;&nbsp; Db db = new Db();<br />\r\n&nbsp;&nbsp;&nbsp; Connection conn = db.getConn();&nbsp;&nbsp;&nbsp;&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp; Statement stmt = conn.createStatement();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp; rs = rwb.getSheet(0);<br />\r\n&nbsp;&nbsp;&nbsp; //e.rwb.close();<br />\r\n&nbsp;&nbsp;&nbsp; int rsRows = rs.getRows();//Cell[] cell = rs.getColumn(0);&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp; System.out.println(rsRows);<br />\r\n&nbsp;&nbsp;&nbsp; StringBuffer sb = null;<br />\r\n&nbsp;&nbsp;&nbsp; for(int i1=0;i1&lt;rsRows;i1++){<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; String[] ii=getExcel(i1);<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; sb = new StringBuffer();<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; sb.append(\"insert into xls(a1,a2,a3,a4,a5,a6,a7,a8) values(\'\");<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; for(int i=0;i&lt;ii.length;i++){&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; sb.append((ii[i]==null?\"\":ii[i])+(i&lt;10?\"\',\'\":\"\"));&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; sb.append(\"\')\");System.out.println();<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; System.out.println(sb);<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; stmt.executeUpdate(sb.toString());<br />\r\n&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp; stmt.close();<br />\r\n&nbsp;&nbsp;&nbsp; conn.close();<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;<br />\r\n}<br />\r\n/**<br />\r\n&nbsp;&nbsp; * @param args<br />\r\n&nbsp;&nbsp; */<br />\r\npublic static void main(String[] args) throws Exception {&nbsp;&nbsp;&nbsp;&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp; //ExeclOper e = new ExeclOper();&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp; //能得到当前类的路径<br />\r\n&nbsp;&nbsp;&nbsp; System.out.println(System.getProperty(\"user.dir\"));<br />\r\n&nbsp;&nbsp;&nbsp; System.out.println(System.getProperty(\"file.separator\"));\r\n</p>\r\n<p style=\"font-family:\'Microsoft YaHei\', Helvetica, Arial, \'Lucida Grande\', Tahoma, sans-serif;font-size:15px;vertical-align:baseline;text-align:justify;text-indent:2em;color:#444444;background-color:#FFFFFF;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;<br />\r\n}<br />\r\n/*<br />\r\nimport java.io.*;<br />\r\nimport jxl.*;<br />\r\nimport jxl.write.*;<br />\r\n… … … …<br />\r\ntry<br />\r\n{<br />\r\n//构建Workbook对象, 只读Workbook对象<br />\r\n//Method 1：创建可写入的Excel工作薄<br />\r\n&nbsp;&nbsp;&nbsp; jxl.write.WritableWorkbook wwb = Workbook.createWorkbook(new File(targetfile));<br />\r\n//Method 2：将WritableWorkbook直接写入到输出流<br />\r\n/*<br />\r\n&nbsp;&nbsp;&nbsp; OutputStream os = new FileOutputStream(targetfile);<br />\r\n&nbsp;&nbsp;&nbsp; jxl.write.WritableWorkbook wwb = Workbook.createWorkbook(os);\r\n</p>\r\n<p style=\"font-family:\'Microsoft YaHei\', Helvetica, Arial, \'Lucida Grande\', Tahoma, sans-serif;font-size:15px;vertical-align:baseline;text-align:justify;text-indent:2em;color:#444444;background-color:#FFFFFF;\">\r\n	}<br />\r\ncatch (Exception e)<br />\r\n{<br />\r\ne.printStackTrace();<br />\r\n}<br />\r\n//创建Excel工作表<br />\r\njxl.write.WritableSheet ws = wwb.createSheet(\"Test Sheet 1\", 0);<br />\r\n//1.添加Label对象<br />\r\njxl.write.Label labelC = new jxl.write.Label(0, 0, \"This is a Label cell\");<br />\r\nws.addCell(labelC);<br />\r\n//添加带有字型Formatting的对象<br />\r\njxl.write.WritableFont wf = new jxl.write.WritableFont(WritableFont.TIMES, 18, WritableFont.BOLD, true);<br />\r\njxl.write.WritableCellFormat wcfF = new jxl.write.WritableCellFormat(wf);<br />\r\njxl.write.Label labelCF = new jxl.write.Label(1, 0, \"This is a Label Cell\", wcfF);<br />\r\nws.addCell(labelCF);<br />\r\n//添加带有字体颜色Formatting的对象<br />\r\njxl.write.WritableFont wfc = new jxl.write.WritableFont(WritableFont.ARIAL, 10, WritableFont.NO_BOLD, false,<br />\r\nUnderlineStyle.NO_UNDERLINE, jxl.format.Colour.RED);<br />\r\njxl.write.WritableCellFormat wcfFC = new jxl.write.WritableCellFormat(wfc);<br />\r\njxl.write.Label labelCFC = new jxl.write.Label(1, 0, \"This is a Label Cell\", wcfFC);<br />\r\nws.addCell(labelCF);<br />\r\n//2.添加Number对象<br />\r\njxl.write.Number labelN = new jxl.write.Number(0, 1, 3.1415926);<br />\r\nws.addCell(labelN);<br />\r\n//添加带有formatting的Number对象<br />\r\njxl.write.NumberFormat nf = new jxl.write.NumberFormat(\"#.##\");<br />\r\njxl.write.WritableCellFormat wcfN = new jxl.write.WritableCellFormat(nf);<br />\r\njxl.write.Number labelNF = new jxl.write.Number(1, 1, 3.1415926, wcfN);<br />\r\nws.addCell(labelNF);<br />\r\n//3.添加Boolean对象<br />\r\njxl.write.Boolean labelB = new jxl.write.Boolean(0, 2, false);<br />\r\nws.addCell(labelB);<br />\r\n//4.添加DateTime对象<br />\r\njxl.write.DateTime labelDT = new jxl.write.DateTime(0, 3, new java.util.Date());<br />\r\nws.addCell(labelDT);<br />\r\n//添加带有formatting的DateFormat对象<br />\r\njxl.write.DateFormat df = new jxl.write.DateFormat(\"dd MM yyyy hh:mm:ss\");<br />\r\njxl.write.WritableCellFormat wcfDF = new jxl.write.WritableCellFormat(df);<br />\r\njxl.write.DateTime labelDTF = new jxl.write.DateTime(1, 3, new java.util.Date(), wcfDF);<br />\r\nws.addCell(labelDTF);<br />\r\n//写入Exel工作表<br />\r\nwwb.write();<br />\r\n//关闭Excel工作薄对象<br />\r\nwwb.close();<br />\r\n//创建只读的Excel工作薄的对象<br />\r\njxl.Workbook rw = jxl.Workbook.getWorkbook(new File(sourcefile));<br />\r\n//创建可写入的Excel工作薄对象<br />\r\njxl.write.WritableWorkbook wwb = Workbook.createWorkbook(new File(targetfile), rw);<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br />\r\n//读取第一张工作表<br />\r\njxl.write.WritableSheet ws = wwb.getSheet(0);<br />\r\n//获得第一个单元格对象<br />\r\njxl.write.WritableCell wc = ws.getWritableCell(0, 0);<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br />\r\n//判断单元格的类型, 做出相应的转化<br />\r\nif(wc.getType() == CellType.LABEL)<br />\r\n{<br />\r\nLabel l = (Label)wc;<br />\r\n&nbsp;&nbsp;&nbsp; l.setString(\"The value has been modified.\");<br />\r\n}<br />\r\n//写入Excel对象<br />\r\nwwb.write();<br />\r\n//关闭可写入的Excel对象<br />\r\nwwb.close();<br />\r\n//关闭只读的Excel对象<br />\r\nrw.close();\r\n</p>\r\n<p style=\"font-family:\'Microsoft YaHei\', Helvetica, Arial, \'Lucida Grande\', Tahoma, sans-serif;font-size:15px;vertical-align:baseline;text-align:justify;text-indent:2em;color:#444444;background-color:#FFFFFF;\">\r\n	*/<br />\r\n}\r\n</p>', '1', '0', '1', '0', '9', '0', '0', '2017-08-24 08:52:22', '0000-00-00 00:00:00');
